import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaPiggyBank, FaPlus, FaTimes, FaBullseye, FaEdit } from 'react-icons/fa';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';
import AddSavings from './AddSavings';
import ChartSavings from './ChartSavings';
import EditSavings from './EditSavings';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ChartDataLabels);

export default function Savings({ dashboardData }) {
    const apiBaseUrl = process.env.REACT_APP_API_URL;

    // States
    const [savings, setSavings] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [showAddForm, setShowAddForm] = useState(false);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);
    const [editingSavingsId, setEditingSavingsId] = useState(null);
    //when goal is completed, show congrats message
    const [congratulationsMessage, setCongratulationsMessage] = useState(null);
    //track if the goal is newly completed or not
    //prevents the congrats message from showing of not newly completed goal
    const [oldCompletedGoal, setOldCompletedGoal] = useState (new Set());

    // Extract goals data from dashboardData with safety checks
    const goalProgress = dashboardData?.goal_progress || [];

    // State to trigger chart updates
    const [chartUpdateTrigger, setChartUpdateTrigger] = useState(0);

    // Function to refresh chart when savings are updated
    const refreshChart = () => {
        setChartUpdateTrigger(prev => prev + 1);
    };

    // Function to check completed goals
    const checkCompletedGoals = async () => {
        try {
            const response = await axios.get(`${apiBaseUrl}/goals/completed`, 
                { withCredentials: true }
            );
            if (response.status === 200 && response.data.message !== 'No completed goals') {
                const currentCompletedGoals = response.data.completed_goals || [];
                // Find newly completed goals (not in previously completed)
                const newlyCompleted = currentCompletedGoals.filter(goal => 
                    !oldCompletedGoal.has(goal.goal)
                );
                // Update the previously completed goals set
                setOldCompletedGoal(new Set(currentCompletedGoals.map(g => g.goal)));
                // Show congratulations only for newly completed goals
                if (newlyCompleted.length > 0) {
                    newlyCompleted.forEach(goal => {
                        setCongratulationsMessage({
                            goal: goal.goal,
                            status: goal.status,
                            message: goal.message
                        });
                    });
                }
            }
        } catch (error) {
            console.error('Error checking completed goals:', error);
        }
    };

    // Fetch savings and goals on mount
    useEffect(() => {
        fetchSavings();
    }, []);

    // Fetch savings from backend
    const fetchSavings = async () => {
        setIsLoading(true);
        setError(null);

        try {
            const response = await axios.get(`${apiBaseUrl}/savings/get_savings`, { 
                withCredentials: true 
            });
            console.log('Savings:', response.data.data);
            if (response.status === 200) {
                setSavings(response.data.data || []);
            }
        } catch (error) {
            console.error('Error fetching savings:', error);
            if (error.response) {
                setError(error.response.data?.error || 'Failed to fetch savings');
            } else {
                setError('Network error. Please try again.');
            }
        } finally {
            setIsLoading(false);
        }
    };


    // Format currency
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('en-NZ', {
            style: 'currency',
            currency: 'NZD'
        }).format(amount || 0);
    };

    // Format date
    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric'
        });
    };

    // Get goal description by ID from goal_progress
    const getGoalDescription = (goalId) => {
        const goal = goalProgress.find(g => g.goal_id === goalId);
        return goal ? goal.description : 'Unknown Goal';
    };

    // Get goal progress info for mini-chart
    const getGoalProgressInfo = (goalId) => {
        const goalInfo = goalProgress.find(g => g.goal_id === goalId);
        return goalInfo;
    };

    // Create mini-chart data for individual cards
    const createCardChartData = (goalId) => {
        const goalInfo = goalProgress.find(g => g.goal_id === goalId);
        const goalAmount = Number(goalInfo?.goal_amount) || 0;

        // Calculate total saved for this goal from all savings
        const totalSaved = savings
            .filter(saving => saving.goal_id === goalId)
            .reduce((sum, saving) => sum + Number(saving.amount || 0), 0);

        const remaining = Math.max(0, goalAmount - totalSaved);

        return {
            labels: ['Progress'],
            datasets: [
                {
                    label: 'Saved',
                    data: [totalSaved],
                    backgroundColor: 'rgba(34, 197, 94, 0.8)',
                    borderColor: 'rgba(34, 197, 94, 1)',
                    borderWidth: 1,
                    borderRadius: 4,
                    stack: 'Stack 0'
                },
                {
                    label: 'Remaining',
                    data: [remaining],
                    backgroundColor: 'rgba(156, 163, 175, 0.7)',
                    borderColor: 'rgba(156, 163, 175, 0.6)',
                    borderWidth: 1,
                    borderRadius: 4,
                    stack: 'Stack 0'
                }
            ]
        };
    };

    // Get mini-chart options for cards
    const getCardChartOptions = (goalId) => {
        const goalInfo = goalProgress.find(g => g.goal_id === goalId);
        const goalAmount = Number(goalInfo?.goal_amount) || 0;

        const totalSaved = savings
            .filter(saving => saving.goal_id === goalId)
            .reduce((sum, saving) => sum + Number(saving.amount || 0), 0);

        return {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
                title: { display: false },
                datalabels: {
                    color: function (context) {
                        return context.datasetIndex === 0 ? 'black' : '';
                    },
                    anchor: 'center',
                    align: 'top',
                    formatter: (value) => value > 0 ? '$' + value.toFixed(0) : '',
                    font: { size: 10, weight: 'bold' }
                },
                legend: { display: false },
            },
            scales: {
                x: { stacked: true, display: false, grid: { display: false } },
                y: { stacked: true, display: false, beginAtZero: true, grid: { display: false } }
            },
            barPercentage: 0.6,
        };
    };

    const handleEditSavings = (savingsId) => {
        setEditingSavingsId(savingsId);
    };
    const handleEditSavingsComplete = () => {
        fetchSavings();
        refreshChart();
        setEditingSavingsId(null);
    };

    const handleCancelEdit = () => {
        setEditingSavingsId(null);
    };

    return (
        <div className="min-h-screen bg-gray-50 p-4 lg:p-8">
            <div className="max-w-5xl mx-auto space-y-6">
                {/* Header */}
                <div className="bg-white shadow-lg rounded-lg overflow-hidden">
                    <div className="bg-gradient-to-r from-lime-900 to-lime-700 p-6">
                        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                            <div>
                                <h2 className="text-2xl lg:text-3xl font-bold text-white">Savings</h2>
                                <p className="text-blue-100 mt-1">Track your savings progress towards your financial goals</p>
                            </div>
                            <button 
                                onClick={() => setShowAddForm(!showAddForm)}
                                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium 
                                rounded-md text-blue-600 bg-white hover:bg-blue-600 hover:text-white transition-colors"
                            >
                                {showAddForm ? <FaTimes className="mr-2" /> : <FaPlus className="mr-2" />}
                                {showAddForm ? 'Cancel' : 'Add Savings'}
                            </button>
                        </div>
                    </div>  
                </div>
                {/* Add Savings Form */}
                {showAddForm && (
                <AddSavings 
                    onSave={async () => {
                        await fetchSavings();
                        refreshChart();
                        // Check for completed goals after adding savings
                        await checkCompletedGoals();
                    }} 
                    onClose={() => setShowAddForm(false)}
                    goals={goalProgress}
                />
                )}
                {/* Messages */}
                {successMessage && (
                    <div className="bg-green-50 border border-green-200 rounded-md p-4">
                        <p className="text-sm font-medium text-green-800">{successMessage}</p>
                    </div>
                )}

                {error && (
                    <div className="bg-red-50 border border-red-200 rounded-md p-4">
                        <p className="text-sm font-medium text-red-800">{error}</p>
                    </div>
                )}

                {/* Congratulations Message */}
                {congratulationsMessage && (
                    <div className="bg-green-50 border-2 border-green-300 rounded-lg p-4 shadow-lg flex items-center justify-between">
                        <div className="flex items-center gap-3">
                            <div>
                                <h3 className="text-lg font-bold text-green-800">
                                    üéâ Congratulations! {congratulationsMessage.goal} Completed! üéâ
                                </h3>
                                {congratulationsMessage.status === 'completed_on_time' && (
                                    <p className="text-green-500 text-sm font-semibold">
                                        ‚≠ê You completed this goal on time! Great job!
                                    </p>
                                )}
                                {congratulationsMessage.status === 'completed_late' && (
                                    <p className="text-yellow-600 text-sm font-semibold">
                                        ‚è∞ You completed this goal after the target date, but better late than never!
                                    </p>
                                )}
                            </div>
                        </div>
                        <button
                            onClick={() => setCongratulationsMessage(null)}
                            className="text-green-400 hover:text-green-600 transition-colors"
                        >
                            <FaTimes className="text-lg" />
                        </button>
                    </div>
                )}

                {/* Loading Spinner */}
                {isLoading ? (
                    <div className="flex items-center justify-center py-12">
                        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
                        <span className="ml-3 text-gray-600">Loading savings...</span>
                    </div>
                ) : (
                    <div>
                        {savings.length === 0 ? (
                            <div className="bg-white shadow-lg rounded-lg p-8 text-center text-gray-500">
                                <FaPiggyBank className="text-4xl mx-auto mb-4 text-gray-300" />
                                <p>No savings recorded yet.</p>
                                <p className="text-sm">Start saving towards your goals!</p>
                            </div>
                        ) : (
                            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                                {savings.map((saving, index) => {
                                    const goalInfo = getGoalProgressInfo(saving.goal_id);
                                    const goalAmount = Number(goalInfo?.goal_amount) || 0;

                                    return (
                                        <div key={index} className="bg-white rounded-lg shadow-md border border-gray-200 p-4 hover:shadow-lg transition-shadow">
                                            {/* Card Header */}
                                            <div className="flex items-center justify-between mb-3">
                                                <div className="flex items-center space-x-2">
                                                    <div className="p-1.5 bg-green-100 rounded-full">
                                                        <FaBullseye className="text-green-600 text-xs" />
                                                    </div>
                                                    <div className="min-w-0 flex-1">
                                                        <h3 className="font-medium text-gray-800 text-sm truncate">
                                                            {saving.description || getGoalDescription(saving.goal_id)}
                                                        </h3>
                                                        <p className="text-xs text-gray-500">
                                                            {formatDate(saving.date)}
                                                        </p>
                                                    </div>
                                                </div>
                                                <button 
                                                    onClick={() => handleEditSavings(saving.savings_id)}
                                                    className="p-1.5 text-blue-600 hover:bg-blue-50 rounded-full transition-colors flex-shrink-0"
                                                >
                                                    <FaEdit className="text-xs" />
                                                </button>
                                            </div>

                                            {/* Mini Chart */}
                                            <div className="mb-4">
                                                <div className="h-24">
                                                    <Bar 
                                                        data={createCardChartData(saving.goal_id)} 
                                                        options={getCardChartOptions(saving.goal_id)} 
                                                    />
                                                </div>
                                            </div>

                                            {/* Amount Info */}
                                            <div className="space-y-1.5 text-xs">
                                                <div className="flex justify-between items-center">
                                                    <span className="text-gray-600">Amount:</span>
                                                    <span className="font-semibold text-green-600">
                                                        <FaPlus className="inline text-xs mr-1" />
                                                        {formatCurrency(saving.amount)}
                                                    </span>
                                                </div>
                                                {goalAmount > 0 && (
                                                    <>
                                                        <div className="flex justify-between items-center">
                                                            <span className="text-gray-600">Goal:</span>
                                                            <span className="font-medium text-blue-600">
                                                                {formatCurrency(goalAmount)}
                                                            </span>
                                                        </div>
                                                        <div className="flex justify-between items-center">
                                                            <span className="text-gray-600">Progress:</span>
                                                            <span className="font-medium text-purple-600">
                                                                {Math.round((savings
                                                                    .filter(s => s.goal_id === saving.goal_id)
                                                                    .reduce((sum, s) => sum + Number(s.amount || 0), 0) / goalAmount) * 100)}%
                                                            </span>
                                                        </div>
                                                    </>
                                                )}
                                            </div>

                                            {/* Edit Form */}
                                            {editingSavingsId === saving.savings_id && (
                                                <div className="mt-4 pt-4 border-t border-gray-200">
                                                    <EditSavings 
                                                        key={`edit-${saving.savings_id}`}
                                                        savings={saving}
                                                        onSave={handleEditSavingsComplete}
                                                        onCancel={handleCancelEdit}
                                                        goalProgress={goalProgress}
                                                    />
                                                </div>
                                            )}
                                        </div>
                                    );
                                })}
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
}
