import React, { useState, useEffect } from 'react';
import { FaEdit, FaTimes } from 'react-icons/fa';
import api from '../api/auth';
import FixedExpense from './FixedExpense';
// import Income from './Income';

function Profile () {
// state management
// 1. profile data
// 2. isEditingProfile
// 3. loading state
//4. error state
// 5. profile form data
// 6. success message 
    const [profileData, setProfileData] = useState(null);
    const [EditingProfile, setEditingProfile] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [ showProfileForm, setShowProfileForm]= useState(false);
    const [successMessage, setSuccessMessage] = useState('')
    const [profileForm, setProfileForm] = useState({
        username: '',
        firstname: '',
        lastname: '',
        email: '',
        old_password: '',
        new_password: '',
        confirm_new_password: ''
    });

// use effect

useEffect (() => { 
    fetchProfileData ()
}, []);

// functions for fetching profile data
    const fetchProfileData = async () => {
        try {
            setLoading(true);
            setError(null);
            const response = await api.get('/user/profile', {
                withCredentials: true
            })
            setProfileData(response.data.data || {});
            // set form data 
            setProfileForm({
                username: response.data.data.username,
                firstname: response.data.data.firstname,
                lastname: response.data.data.lastname,
                email: response.data.data.email,
                old_password: '',
                new_password: '',
                confirm_new_password: ''
            })
        } catch (error) {
            setError(error.response.data.error || 'Failed to fetch profile data');
        } finally {
            setLoading(false);
        }
    }

// function to handle profile update
const handleProfileUpdate = async (e) => {
    e.preventDefault(e);
    try {
        setError(null)
        setSuccessMessage ('')
        // submit the form data to the backend
        const response = await api.put('user/update', profileForm, {
            withCredentials: true
        })
        //  if successfull, update profile and show success message
        // set the editing profile to false and show the updated data
        setSuccessMessage('Profile updated successfully');
        setEditingProfile(false);
        // Update profile data directly without refetching
        setProfileData(prevData => ({
            ...prevData,
            ...profileForm
        }));
        // Clear password fields after successful update
        setProfileForm(prev => ({
            ...prev,
            old_password: '',
            new_password: '',
            confirm_new_password: ''
        }));
        // clear the success message after 3 seconds
        setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
        console.error('Error updating profile:', err);
        setError(err.response.data.error || 'Failed to update profile');
        // clear the error message after 5 seconds
        setTimeout(() => setError(null), 3000);
    } finally {
        setLoading(false);
    } 
}

