import logoWhiteText from '../static/logoWhiteText.png';
import { Link, useLocation } from 'react-router-dom';

function Header({user}) {
  const location = useLocation();

  const handleNavClick = (path) => {
    if (location.pathname === path) {
      window.location.reload();
    }
  };

  return (
    <header className="flex items-center justify-between bg-lime-900 px-4 sm:px-8 lg:px-12 h-20 sm:h-28">
        <div className="flex items-center">
            <img className="w-32 h-16 sm:w-64 sm:h-60 object-contain" src={logoWhiteText} alt="logo" />
        </div>

        {/* Navigation
        <nav className="flex gap-2 sm:gap-4 lg:gap-8 pr-2 sm:pr-4">
          {user ? (
            <>
            <Link to="/dashboard" onClick={() => handleNavClick('/dashboard')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Dashboard</Link>
              <Link to="/whatif" onClick={() => handleNavClick('/whatif')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Simulation Playground</Link>
              <Link to="/expenses" onClick={() => handleNavClick('/expenses')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Expenses</Link>
              <Link to="/goals" onClick={() => handleNavClick('/goals')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Goals</Link>
              <Link to="/addsavings" onClick={() => handleNavClick('/addsavings')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Savings</Link>
              <Link to="/profile" onClick={() => handleNavClick('/profile')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Profile</Link>
              <Link to="/logout" onClick={() => handleNavClick('/logout')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Logout</Link>
            </>
          ) : (
            <>
              <Link to="/" onClick={() => handleNavClick('/')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">About Us</Link>
              <Link to="/" onClick={() => handleNavClick('/')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">What We Do</Link>
              <Link to="/register" onClick={() => handleNavClick('/register')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Register</Link>
              <Link to="/login" onClick={() => handleNavClick('/login')} className="text-white hover:text-gray-300 text-xs sm:text-sm lg:text-lg font-semibold transition-colors duration-200">Login</Link>
            </>
          )} */}
        {/* </nav> */}
    </header>
  );
}

export default Header;
