// import the expense chart 
// make chart respond to the sliders 
// 1. chart shows original expense by category per month 
// a. need to have data and month, 
// 2. chart changes as slider gets adjusted 

import { useState, useEffect } from 'react'
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend} from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ChartDataLabels);


export default function WhatIfChart ({ categories, savings, selectedMonth }) {
// chart data
// fetch expenses from backend state 
    const [monthlyExpenseCategory, setMonthlyExpenseCategory ] = useState ([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const barBGColor= [  
        // 'rgba(255, 99, 132, 0.8)' 
        'rgba(54, 162, 235, 0.8)' 
        // 'rgba(255, 206, 86, 0.8)',  
        // 'rgba(75, 192, 192, 0.8)', 
        // 'rgba(153, 102, 255, 0.8)',
        // 'rgba(255, 159, 64, 0.8)', 
        // 'rgba(255, 99, 132, 0.8)',  
        // 'rgba(201, 203, 207, 0.8)'  
    ]

    const apiBaseUrl = process.env.REACT_APP_API_URL;
    const fetchMonthlyExpenseCategory = async () => {
        try {
            setIsLoading (true);
            setError(null);
            // Check if selectedMonth is defined
            if (!selectedMonth) {
                console.error('selectedMonth is undefined or null');
                setError('No month selected');
                setIsLoading(false);
                return;
            }
            const response  = await axios.get( `${apiBaseUrl}/whatif/expense_categories/${selectedMonth}`,
                { withCredentials: true}
            ) 
            console.log ('Chart data:', response.data.categories);
            setMonthlyExpenseCategory (response.data.categories)
            setIsLoading(false);
        } catch (error) {
            console.error('Error fetching monthly category expenses:', error);
            setError(error.message);
            setIsLoading(false);
        }
    }
//  using the monthlyExpence category cat_id matach savings.category_breakdown cat_id
// then check if there is a new_amount !== 0 or undefined., return the new_amount value into the chart
// else original amount.
    const barData = () => {
        return monthlyExpenseCategory.map((expense, index) => {
            // Find the matching category in category_breakdown
            const breakdown = savings.category_breakdown.find(item => item.category_id === expense.category_id);

            // If there's a match AND there's a new_amount, return the new_amount
            if (breakdown && breakdown.new_amount !== undefined) {
                return breakdown.new_amount;
            } else {
                // Otherwise return the original amount
                return expense.original_amount;
            }
        });
    }

    useEffect( () => {
        fetchMonthlyExpenseCategory();
    }, [categories, savings])

    // make the Y axis responive for smaller spendings
    // using the max data plus 20% so it scales according to the data
    function yAxis () {
        const originalAmounts = monthlyExpenseCategory.map(expense => expense.original_amount);
        const adjustedAmount = barData();
        const originalMax = Math.max(...originalAmounts);
        const adjustedMax = Math.max(...adjustedAmount);

        // Compare and use the higher amount
        const higherAmount = Math.max(originalMax, adjustedMax);
        return Math.ceil(higherAmount * 1.2);
        }

    // define the chart 
    const chartData = {
        labels: monthlyExpenseCategory.map(expense => expense.category_name),
        // 2 bars - 1 for original , 1 for adjusted to show the difference (visual)
        datasets: [{
            // For Orginal amount
            label: 'Original Amount',
            data: monthlyExpenseCategory.map(expense => expense.original_amount),
            backgroundColor: 'rgba(197, 198, 200, 0.8)',
            borderRadius:4
            },{ 
            //For  Adjusted amount
                // define the X axis dataset for adjusted
                label: 'Adjusted Amount',
                // if there is a new_amount, reflect that else the original
                data: barData (),
                // cycle through the barBGCOlor if user has more than 7 categories per month
                backgroundColor: monthlyExpenseCategory.map((_, index) => barBGColor[index % barBGColor.length]),
                borderRadius: 4
            }]   
        }
      // chart options
      const chartOptions = {
        responsive: true,
        maintainAspectRatio: false,
        layout: {
            padding: {
                top: 0,      // Space above chart
                bottom: 0,   // Space below chart
                left: 0,     // Space left of chart
                right: 0     // Space right of chart
            }
        },
        plugins: {
            title: {display: true,
                text: `Expenses by Category - ${selectedMonth}`,
                font: {
                    size: 16,
                    weight: 'bold'
                }},
                datalabels :{
                    color:'black',
                    anchor: 'end',
                    align: 'top',
                    formatter: (value) => '$' + value,
                    font:{ size: 12 } },
            legend: {display: true }
        },
        scales: {
            y: {
                display: false,
                max: yAxis()
            },
            x: {grid: {display: false}}
        }
    }


    return (
        <div className="bg-white shadow-lg rounded-lg p-6 h-full">
            {/* <h4 className="text-lg font-semibold mb-4 text-gray-800">
                Expense Comparison for {selectedMonth}
            </h4> */}
            {isLoading || !chartOptions ? (
            <div className="flex items-center justify-center py-12">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"> </div>
                <span className="ml-3 text-gray-600">Loading charts</span>
            </div>
        ) : (
            <div className='h-96'>
                <Bar data={chartData} options={chartOptions} />
            </div>
            ) }

            {/* closing main tag */}
        </div>
    )
}
